const initState = {
    themeId: 1,
}


export const themeReducer = (state = initState, action: ChangeThemeType): typeof initState => { // fix any
    switch (action.type) {
        // дописать
        case "SET_THEME_ID": {
            return {
                ...state,
                themeId: action.id
            }
        }
        default:
            return state
    }
}

export const changeThemeId = (id: number) => ({type: 'SET_THEME_ID', id: id}) // fix any

export type ChangeThemeType = {
    type: 'SET_THEME_ID',
    id: number
}
